    // const handleOpen = () => setIsOpen(true);
    // const handleClose = () => setIsOpen(false);


  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('address', address);
    formData.append('email', email);
    formData.append('phone', phone);
    formData.append('price', price);
    formData.append('currency', currency);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await fetch('http://localhost:5000/api/hotels/add', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Hotel added successfully:', result);
        // Redirection ou mise à jour de l'interface utilisateur
      } else {
        const errorData = await response.json();
        console.error('Failed to add hotel:', errorData.message);
      }
    } catch (error) {
      console.error('Error adding hotel:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Nom de l'hôtel</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Adresse</label>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Numéro de téléphone</label>
        <input
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Prix par nuit</label>
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Devise</label>
        <select value={currency} onChange={(e) => setCurrency(e.target.value)} required>
          <option value="">Sélectionnez une devise</option>
          <option value="XOF">F XOF</option>
          <option value="EUR">Euro</option>
          <option value="USD">Dollar</option>
        </select>
      </div>
      <div>
        <label>Ajouter une photo</label>
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          accept="image/*"
        />
      </div>
      <button type="submit">Enregistrer</button>
    </form>
  );
};

