import './Form.css';
import React from 'react';
import  vector from './vector.png';
import { useState } from 'react';
import axios from 'axios';


export default LoginForm = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = (e) => {
    e.preventDefault()
    router.push('http://localhost:5000/loginform', {email, password})
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
  })

  return (
    
    <div>



      <div className="mt-20  flex items-center gap-2 px-1">
      <img alt='' src={vector} className='  w-3 '></img>
        <h1 className='font-bold '>RED PRODUCT</h1>
      </div>
      <div className='bg-white  pb-8 w-50  text-align: left;' id='block'>  
        <div className="mt-10 text-align: left;">
          
          <div className="mt-5 text-align: left; ml-2.5">
          
            <div className="">
              <div className="">
                <form  onSubmit={handleSubmit} action="border-inherit" >
                  <p className="">Connectez-vous en tant que Admin</p>
                  <div className='border-inherit'>
                  <div className='input-box mt-3'>
                      <input type="malito " className=' mt-3 w-full border-b-2' placeholder='E-mail' 
                        autoComplete='off'
                        onChange={(e) => setEmail(e.target.value)}
                      />
                  </div>
                  </div>
                  
                  <div className="">
                    <div className='input-box mt-3' >
                        <input type="password  " className='mt-3 w-full  border-b-2' placeholder='Mot de passe'
                          autoComplete='off'
                          onChange={(e) => setPassword(e.target.value)} 
                        />
                    </div>
                    <div className='remember-forgot mt-2'>
                        <label><input type="checkbox" className='mt-3 w-8 h-4' />Gardez-moi connecté</label>
                    </div>
                  </div>
                  
                  <div className=" mt-3 border bg-black 500 text-center  ml-10 mr-10 h-8 ">
                  <button type="submit" className="text-white">Se connecter</button>
                  </div>
                  
                  
                </form>
              </div>
            </div>
              
          
          </div>
          
        </div>
    
               
        </div>



        
        <div className='text-center text-yellow-300' >
          <a href="/PasseOublie" >Mot de passe oublié?</a>
        </div>

        <p className='text-center text-white'>Vous n'avez pas de compte? <a href="/Connexion" className='text-yellow-300'>S'inscrire</a></p>
      </div>




    

  
    
  );
}
}
