import React, { useState } from 'react'
import Victor from './Victor.png'
import axios from 'axios'
const [user,setUser] = useState(null)


export const Connexion = () => {
  const [email, setEmail] = useState()
  const [password, setPassword] = useState()
  

  const handleSignIn = async () => {
    try {
      axios({
        method: "post",
        url: "http://localhost:5000/api/users/login",
        withCredentials: true,
        data: {
          email,
          password,
        },
      }).then((res) => {
        setEmail("");
        setPassword("");
        setUser(res.data);
        // console.log(res.data);
          window.localStorage.setItem("customer", JSON.stringify(res.data));
          window.location.href = "/dashbord";
      });
    } catch ({ response }) {
      notifyBackErrors(response.data.message);
    }
  };
  return (
    <div>



      <div className="mt-20 text-center ">
        
        <h1 className='font-bold '><img alt='' src={Victor} className=' text-align-center w-3 absolute  left-1/3 '></img>RED PRODUCT</h1>
      </div>
      <div className='bg-white  pb-8 w-50  text-align: left;' id='block'>  
        <div className="mt-10 text-align: left;">
          
          <div className="mt-5 text-align: left; ml-2.5">
          
            <div className="">
              <div className="">
                <form  action="border-inherit" >
                  <p className="">Connectez-vous en tant que Admin</p>
                  <div className='border-inherit'>
                  <div className='input-box mt-3'>
                      <input type="text " className='py-3 w-full border-b-2' placeholder='Nom' />
                  </div>
                  <div className='input-box mt-3'>
                      <input type="malito " className='py-3 w-full border-b-2' placeholder='E-mail' 
                      autoComplete='off'
                      onChange={(e) => setEmail(e.target.value)}
                      />
                  </div>
                  </div>
                  
                  <div className="">
                    <div className='input-box mt-3' >
                        <input type="password  " className='w-full py-3  border-b-2' placeholder='Mot de passe' 
                        autoComplete='off'
                        onChange={(e) => setPassword(e.target.value)}
                        />
                        
                    </div>
                    <div className='remember-forgot mt-3'>
                        <label><input type="checkbox" className='w-8 h-4' />Accepter les termes et la politique</label>
                    </div>
                  </div>
                  
                  <div className="mt-2 border bg-black 500 text-center  ml-10 mr-10 h-8 ">
                  <button  onClick={handleSignIn} type="submit" className="text-white">Se connecter</button>
                  </div>
                  
                  
                </form>
              </div>
            </div>
              
          
          </div>
          
        </div>
    
               
        </div>



        
        <div className='text-center text-yellow-300' >
          <a href="/PasseOublie" >Mot de passe oublié?</a>
        </div>

        <p className='text-center text-white'>Vous n'avez pas de compte? <a href="Connexion" className='text-yellow-300'>S'inscrire</a></p>
      </div>
  )
}
